import React from 'react';
import PropType from 'prop-types';
import styled from 'styled-components';

import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Container = styled.div`
  margin: 15px 15px 25px 0px;
  width: 90%;
`;

const ImageContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Image = styled.img`
  max-width: 400px;
  max-height: 430px;
  width: 100%;
  height: 100%;
`;

const PostImages = ({ images }) => {
  const settings = {
    arrows: false,
    dots: true,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
  }

  if (images.length === 1) {
    return (
      <Container>
        <ImageContainer>
          <Image src={`http://localhost:3065/postImages/${images[0].src}`} alt="image" />
        </ImageContainer>
      </Container>
    );
  };

  if (images.length > 1) {
    return (
      <Container>
        <Slider {...settings}>
          {images.map(image => (
            <div key={image.src}>
              <ImageContainer>
                <Image src={`http://localhost:3065/postImages/${image.src}`} alt="image" key={image.src} />
              </ImageContainer>
            </div>
          ))}
        </Slider>
      </Container>
    )
  };
};

PostImages.propType = {
  images: PropType.object.isRequired,
};

export default PostImages;
