import React from 'react';
import styled from 'styled-components';

import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Container = styled.div`
  margin-top: 5px;
  overflow: hidden;
`;

const StyledSlider = styled(Slider)`
  .slick-slide div{
    outline: none;
  }
`;

const ImageContainer = styled.div`
  margin: 0 16px;
`;

const Image = styled.img`
  max-width: 400px;
  width: 100%;
  height: 500px;
  border-radius: 5px;
`;

const PostImages = ({ images }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
    centerMode: true,
  }

  if (images.length === 1) {
    return (
      <Container>
        <Image src={images[0].src} alt={images[0].src} />
      </Container>
    );
  };

  if (images.length > 1) {
    return (
      <Container>
        <Slider {...settings}>
          {images.map((image) => (
            <div key={image.src}>
              <Image src={image.src} />
            </div>
          ))}
        </Slider>
      </Container>
    );
  }
};

export default PostImages;
