import React from 'react';
import styled from 'styled-components';
import ProfileImage from './ProfileImage';

import { FaTrashAlt, FaRegCommentDots } from "react-icons/fa";

const Container = styled.div`
  width: 100%;
  max-height: 700px;
  border-bottom: 5px solid #e6ecf0;
  padding: 10px;
`; 

const UpsideContainer = styled.div`
  display: flex;
  width: 100%;
`;

const PostInformationContainer = styled.div`
  width: 100%;
  display: flex;
  margin-left: 10px;
  margin-top: 3px;
`;

const ProfileImageContainer = styled.div`
  width: 70px;
  height: 60px;
  margin-right: 10px;
  border-radius: 50px;
  overflow: hidden;
`;

const Nickname = styled.span`
  font-size: 18px;
  font-weight: 600;
`;

const Date = styled.span`
`;

const DeleteButton = styled.div`
  display: flex;
  justify-content: center;
  color: #e8537c;
  opacity: 0.6;
  font-size: 15px;
  cursor: pointer;
  padding: 10px;
  border-radius: 50%;
  margin-right: 10px;
  transition: all 0.2s linear;
  &:hover {
    opacity: 1;
  }
`;

const PostCard = ({ post }) => {
  return (
    post && (
      <Container>
        <UpsideContainer>
          <ProfileImageContainer>
            <ProfileImage src={require('../image/user.png')} /> 
          </ProfileImageContainer>
          <PostInformationContainer>
            <Nickname>{ post.User.nickname }</Nickname>
          </PostInformationContainer>
          <DeleteButton>
            <FaTrashAlt />
          </DeleteButton>
        </UpsideContainer>
      </Container>
    )
  )
};

export default PostCard;
