import React, { useCallback, useRef, useState } from 'react';
import styled from 'styled-components';
import { useDispatch } from 'react-redux';
import { CHANGE_PROFILE_IMAGE_REQUEST } from '../reducers/user';
import { BsPlusCircleFill } from 'react-icons/bs';

const Form = styled.form`
  margin-left: 100px;
`;

const PlusIconContainer = styled.div`
  font-size: 27px;
  color: #1864ab;
  opacity: 0.6;
  cursor: pointer;
  :hover {
    opacity: 1;
  }
`;

const ProfileImageEditForm = () => {
  const dispatch = useDispatch();

  const imageInput = useRef();
  const onClickImageUpload = useCallback(() => {
    imageInput.current.click();
  }, [imageInput.current]);

  const formData = new FormData();

  const onChangeImage = useCallback((e) => {
    console.log('profileImage', e.target.files);
    formData.append('profileImage', e.target.files);

    onSubmit();
  }, []);

  const onSubmit = useCallback(() => {
    return dispatch({
      type: CHANGE_PROFILE_IMAGE_REQUEST,
      data: formData,
    });
  }, []);

  return (
    <Form onSubmit={onSubmit} encType="multipart/form-data">
      <input
        type="file"
        name="profileImage"
        hidden
        ref={imageInput}
        onChange={onChangeImage}
      />
      <PlusIconContainer onClick={onClickImageUpload}>
        <BsPlusCircleFill />
      </PlusIconContainer>
    </Form>
  );
};

export default ProfileImageEditForm;
