import React, { useCallback, useEffect, useState } from 'react';
import PropType from 'prop-types';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import TextArea from 'react-textarea-autosize';
import { ADD_COMMENT_REQUEST } from '../reducers/post';
import device from '../utils/device';

const Container = styled.div`
  width: 100%;
  max-height: 500px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Form = styled.form`
  width: 100%;
  min-height: 50px;
  padding: 10px;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const ContentInput = styled(TextArea)`
  width: 80%;
  border: none;
  padding: 8px;
  font-size: 16px;
  min-height: 40px;
  max-height: 300px;
  border-radius: 10px;
  background-color: #f8f9fa;

  @media ${device.mobileL} {
    width: 72%;
  }
`;

const SubmitButton = styled.button`
  all: unset;
  position: absolute;
  background-color: #1864ab;
  color: white;
  letter-spacing: 2px;
  font-weight: 600;
  font-size: 14px;
  width: 70px;
  height: 30px;
  border-radius: 6px;
  bottom: 22px;
  right: 35px;
  cursor: pointer;
  opacity: ${props => (props.isAvailableUpload ? 1 : 0.6)};
  transition: opacity 0.3s linear;
  display: flex;
  justify-content: center;
  align-items: center;

  @media ${device.mobileL} {
    font-size: 12px;
    width: 50px;
    height: 30px;
  }
`;

const CommentForm = ({ post }) => {
  const dispatch = useDispatch();
  const id = useSelector((state) => state.user.me?.id);
  const { addCommentDone } = useSelector((state) => state.post);
  
  const [content, setContent] = useState('');
  const [isAvailablePosting, setIsAvailablePosting] = useState(false);

  useEffect(() => {
    if (addCommentDone) {
      setContent('');
    }
  }, [addCommentDone])

  const onChangeContent = useCallback((e) => {
    const {target: {value}} = e;
    setContent(e.target.value);
    if (value.trim()) {
      setIsAvailablePosting(true);
    } else if (value.length === 0 || value.length > 500) {
      setIsAvailablePosting(false);
    };
  }, [content]);

  const onSubmit = useCallback((e) => {
    e.preventDefault();
    if (content.length > 500) {
      alert("글자수가 너무 많습니다.");
      return;
    }
    if (!content || !content.trim()) {
      return alert("댓글을 작성해주세요.");
    }
    dispatch({
      type: ADD_COMMENT_REQUEST,
      data: { 
        content: content, 
        postId: post.id, 
        userId: id 
      }
    });
  }, [content])

  return (
    <Container>
      <Form onSubmit={onSubmit}>
        <ContentInput
          placeholder={id ? "댓글을 남겨주세요." : "로그인 후 이용해주세요."}
          disabled={id ? false : true}
          value={content}
          onChange={onChangeContent }
          style={{
            resize: "none",
            outline: "none",
            overflow: "hidden",
          }}
        />
        <SubmitButton type="submit" isAvailableUpload={isAvailablePosting}>입력</SubmitButton>
      </Form>
    </Container>
  );
};

CommentForm.propType = {
  post: PropType.object.isRequired,
};

export default CommentForm;
