import React, { useCallback, useRef, useState } from 'react';
import PropType from 'prop-types';
import styled from 'styled-components';
import ProfileImage from './ProfileImage';
import IntroduceEditForm from './IntroduceEditForm';
import { BsPlusCircleFill, BsPencilFill } from 'react-icons/bs';

const Container = styled.div`
  width: 700px;
  height: 400px;
  background-color: white;
  border-radius: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);
`;

const ProfileImageContainer = styled.div`
  margin-top: 50px;
  margin-bottom: -40px;
  width: 150px;
  height: 150px;
  border-radius: 70%;
  overflow: hidden;
`;

const Form = styled.form`
  margin-left: 100px;
`;

const PlusIconContainer = styled.div`
  font-size: 27px;
  color: #1864ab;
  opacity: 0.6;
  cursor: pointer;
  :hover {
    opacity: 1;
  }
`;

const NicknameContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  margin-top: 15px;
  margin-bottom: 5px;
`;

const Nickname = styled.div`
  font-weight: 600;
  font-size: 25px;
  color: #212529;
`;

const IntroduceContainer = styled.div`
  display: flex;
  justify-content: center;
`;

const Introduce = styled.div`
  margin-right: 7px;
  font-weight: 500;
  font-size: 18px;
  color: #868e96;
`;

const PencileIconContainer = styled.div`
  font-size: 15px;
  margin-top: 2px;
  opacity: 0.6;
  cursor: pointer;
  :hover {
    opacity: 1;
  }
`;

const Button = styled.button`
  all: unset;
  border-radius: 10px;
  width: 20%;
  height: 30px;
  padding: 8px 15px;
  margin-top: 25px;
  background-color: #1864ab;
  color: white;
  font-weight: 700;
  font-size: 17px;
  text-align: center;
  cursor: pointer;
  opacity: 0.8;
  outline: none;
  &:hover {
    opacity: 1;
  }
`;

const UserProfile = ({ me }) => {
  const [onClickEditForm, setOnClickEditForm] = useState(false);

  const imageInput = useRef();
  const onClickImageUpload = useCallback(() => {
    imageInput.current.click();
  }, [imageInput.current]);

  const onChangeImages = useCallback((e) => {
    const imageFormData = new FormData();
    [].forEach.call(e.target.files, f => {
      imageFormData.append("image", f);
    });

    console.log(imageFormData);
  });


  const onSubmitProfileImage = useCallback(() => {

  }, []);

  const onSubmitIntroduce = useCallback(() => {

  }, []);

  return (
    <Container>
      <ProfileImageContainer>
        <ProfileImage />
      </ProfileImageContainer>
      <Form onSubmit={onSubmitProfileImage} encType="multipart/form-data">
        <input
          type="file"
          multiple
          hidden
          ref={imageInput}
          onChange={onChangeImages}
        />
        <PlusIconContainer onClick={onClickImageUpload}>
          <BsPlusCircleFill />
        </PlusIconContainer>
      </Form>
      <NicknameContainer>
        <Nickname>{ me.nickname }</Nickname>
      </NicknameContainer>
      <IntroduceContainer>
        <Introduce>{ me.introduce }</Introduce>
        <PencileIconContainer>
          <BsPencilFill />
        </PencileIconContainer>
      </IntroduceContainer>
      <Button>
        내 게시글
      </Button>
    </Container>
  )
};

export default UserProfile;
