import React from "react";
import PropTypes from 'prop-types';

const PostContent = ({ content }) => {
  return (
    <div>
      {content.split(/(#[^\s#]+)/g)}
    </div>
  )
};

PostContent.propTypes = {
  content: PropTypes.string.isRequired,
}

export default PostContent;
