import React, { useCallback, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import ProfileImage from './ProfileImage';
import PostImages from './PostImages';
import PostContent from './PostContent';
import Comment from './Comment';
import CommentForm from './CommentForm';
import { REMOVE_POST_REQUEST } from '../reducers/post';
import { FaTrashAlt, FaRegCommentDots } from "react-icons/fa";
import { IoIosHeartEmpty, IoIosHeart } from "react-icons/io";

const Container = styled.div`
  width: 100%;
  border-bottom: 5px solid #e6ecf0;
  padding: 10px;
`; 

const ProfileImageContainer = styled.div`
  width: 55px;
  height: 55px;
  margin-right: 18px;
  border-radius: 50px;
  overflow: hidden;
  float: left;
`;

const PostInformationContainer = styled.div`
  height: 52px;
  display: flex;
  justify-content: space-between;
  margin-left: 10px;
  margin-top: 3px;
`;

const Nickname = styled.span`
  margin-top: 3px;
  font-size: 18px;
  font-weight: 600;
`;

const Date = styled.span`
`;

const DeleteButton = styled.div`
  display: flex;
  color: #e8537c;
  opacity: 0.6;
  font-size: 18px;
  cursor: pointer;
  padding: 10px;
  border-radius: 50%;
  margin-right: 16px;
  transition: all 0.2s linear;
  &:hover {
    opacity: 1;
  }
`;

const ContentContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const Content = styled.span`
  width: 88%;
  overflow: auto;
  min-height: 20px;
  margin-top: 20px;
  white-space: pre;
  line-height: 18px;
`;

const UnderSideContainer = styled.div`
  display: flex;
  justify-content: end;
  align-items: center;
  margin-top: 10px;
  margin-right: 20px;
`;

const IconsContainer = styled.div`
  color: ${props => props.color};
  opacity: 0.7;
  padding: 7px;
  border-radius: 50%;
  font-size: 20px;
  transition: all 0.1s linear;
  cursor: pointer;
  &:hover {
    opacity: 1;
  }
`;

const Amount = styled.div`
  margin-left: -2px;
  margin-bottom: 5px;
  font-size: 16px;
  opacity: 0.7;
`;

const CommentContainer = styled.div`
  width: 1500px;
  max-height: 500px;
  margin-left: -15px;
  margin-top: 10px;
  padding-top: 10px;
  border-top: 2px solid #e6ecf0;
`;

const Post = ({ post }) => {
  const dispatch = useDispatch();
  const id = useSelector((state) => state.user.me?.id);
  const [liked, setLiked] = useState(false);
  const [commentFormOpend, setCommentFormOpend] = useState(false);
  
  const onRemovePost = useCallback(() => {
    dispatch({
      type: REMOVE_POST_REQUEST,
      data: post.id,
    })
  }, []);

  const onToggleLike = useCallback(() => {
    setLiked((prev) => !prev);
  }, [liked]);

  const onToggleComment = useCallback(() => {
    setCommentFormOpend((prev) => !prev);
  }, [commentFormOpend]);

  return (
    post && (
      <Container>
        <ProfileImageContainer>
          <ProfileImage src={require('../image/user.png')} />
        </ProfileImageContainer>
        <PostInformationContainer>
          <Nickname>{post.User.nickname}</Nickname>
          {id && post.User.id === parseInt(id)
            ? ( <DeleteButton onClick={onRemovePost}>
                  <FaTrashAlt />  게시물 삭제 15분
                </DeleteButton> )
            : null 
          }
        </PostInformationContainer>
        <ContentContainer>
          {post.Images && <PostImages images={post.Images} />}
          <Content>
            <PostContent content={post.content} />
          </Content>
        </ContentContainer>
        <UnderSideContainer>
          <IconsContainer onClick={onToggleLike}>
            {liked 
             ? <IoIosHeartEmpty color="red" />
             : <IoIosHeart color="red" />
            }
          </IconsContainer>
          <IconsContainer onClick={onToggleComment}>
            <FaRegCommentDots color="#3498db"/>
          </IconsContainer>
          <Amount>{post.Comments.length}</Amount>
        </UnderSideContainer>
        {commentFormOpend && (
          <>
            <CommentContainer>
              {post.Comments.map((v) => <Comment comment={v} />)}
            </CommentContainer>
            <CommentForm post={post}/>
          </>
        )}
      </Container>
    )
  )
};

export default Post;
