import React from 'react';
import { useSelector } from 'react-redux';
import styled from 'styled-components';
import ProfileImage from './ProfileImage';
import PostImages from './PostImages';
import { FaTrashAlt, FaRegCommentDots } from "react-icons/fa";
import { IoIosHeartEmpty, IoIosHeart } from "react-icons/io";

const Container = styled.div`
  width: 100%;
  max-height: 700px;
  border-bottom: 5px solid #e6ecf0;
  padding: 10px;
`; 

const UpsideContainer = styled.div`
  display: flex;
  width: 100%;
`;

const PostInformationContainer = styled.div`
  width: 100%;
  display: flex;
  margin-left: 10px;
  margin-top: 3px;
`;

const ProfileImageContainer = styled.div`
  width: 75px;
  height: 60px;
  margin-right: 10px;
  border-radius: 50px;
  overflow: hidden;
`;

const Nickname = styled.span`
  font-size: 18px;
  font-weight: 600;
`;

const Date = styled.span`
`;

const DeleteButton = styled.div`
  display: flex;
  justify-content: center;
  color: #e8537c;
  opacity: 0.6;
  font-size: 17px;
  cursor: pointer;
  padding: 10px;
  border-radius: 50%;
  margin-right: 10px;
  transition: all 0.2s linear;
  &:hover {
    opacity: 1;
  }
`;

const ContentContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const Description = styled.span`
  width: 85%;
  padding-left: 10px;
  overflow: auto;
  min-height: 20px;
  margin-top: 20px;
  white-space: pre;
  line-height: 18px;
  -ms-overflow-style: none; // IE에서 스크롤바 감춤
  &::-webkit-scrollbar {
    display: none !important; // 윈도우 크롬 등
  }
`;

const UnderSideContainer = styled.div`
  display: flex;
  justify-content: end;
  align-items: center;
  margin-top: 10px;
`;



const PostCard = ({ post }) => {
  const id = useSelector((state) => state.user.me?.id);
  
  return (
    post && (
      <Container>
        <UpsideContainer>
          <ProfileImageContainer>
            <ProfileImage src={require('../image/user.png')} /> 
          </ProfileImageContainer>
          <PostInformationContainer>
            <Nickname>{ post.User.nickname }</Nickname>
          </PostInformationContainer>
          {id && post.User.id === id
            ? (
            <DeleteButton>
              <FaTrashAlt />
            </DeleteButton>
            ) : 
            null }
        </UpsideContainer>
        <ContentContainer>
          {post.Images[0] && <PostImages images={post.Images} />}
          <Description>
            {post.content}
          </Description>
        </ContentContainer>
        <UnderSideContainer>
          <IoIosHeartEmpty />
          <FaRegCommentDots />
        </UnderSideContainer>
      </Container>
    )
  )
};

export default PostCard;
