import React from 'react';
import styled from 'styled-components';

import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Container = styled.div`
  display: flex,
  margin-top: 5px;
  overflow: hidden;
  justifyContent: center,
  alignItems: center,
  width: 100%;
`;

const Image = styled.img`
  max-width: 400px;
  width: 100%;
  height: 500px;
  border-radius: 5px;
`;

const PostImages = ({ images }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
  }

  if (images.length === 1) {
    return (
      <Container>
        <Image src={images[0].src} alt={images[0].src} />
      </Container>
    );
  };

  if (images.length > 1) {
    return (
      <Container>
        <Slider {...settings}>
          {/* {images.map((image) => (
            <div>
              <Image src={image.src} alt="image" />
            </div>
          ))} */}
          <div>
            <h3>1</h3>
          </div>
          <div>
            <h3>2</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
        </Slider>
      </Container>
    );
  };
};

export default PostImages;
