import React from 'react';
import styled from 'styled-components';
import device from '../utils/device';

import Slider from "react-slick";
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const Container = styled.div`
  margin: 15px 15px 15px 0px;
  width: 90%;
  overflow: hidden;
`;

// const StyledSlider = styled(Slider)`
//   .slick-slide div {
//     outline: none;
//   }
// `;

const StyledSlider = styled(Slider)`
  height: 100%;
  .slick-list {
      height: 100%;
      object-fit: cover;
      display: flex; 
      align-items: center;
  }
  .slick-track {
      display: flex;
      align-items: center;
  }
  .slick-prev {
      left: 6px;
      z-index: 999;
  }
  .slick-next {
      right: 6px;
      z-index: 999;
  }
`;

const ImageContainer = styled.div`
//   margin-left: 100px;

//   @media ${device.tablet} {
//     margin-left: 140px;
//   }

//   @media ${device.mobileL} {
//     margin-left: 0px;
//   }
`;

const Image = styled.img`
  max-width: 400px;
  width: 380px;
  height: 500px;
`;

const PostImages = ({ images }) => {
  const settings = {
    arrows: false,
    dots: true,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
  }

  if (images.length === 1) {
    return (
      <Container>
        <Image src={images[0].src} alt="image" />
      </Container>
    );
  };

  if (images.length > 1) {
    return (
      <Container>
        <StyledSlider {...settings}>
          {images.map(image => (
            <div key={image.src}>
              <ImageContainer>
                <Image src={image.src} alt="image" key={image.src} />
              </ImageContainer>
            </div>
          ))}
        </StyledSlider>
      </Container>
    );
  };
};

export default PostImages;
