import React, { useCallback, useRef, useState } from "react";
import { useSelector } from "react-redux";
import styled from 'styled-components';
import TextArea from 'react-textarea-autosize';
import { FaRegImage } from 'react-icons/fa';

const Container = styled.div`
  width: 100%;
  max-height: 500px;
`;

const FormContainer = styled.form`
  width: 100%;
  min-height: 120px;
  border-bottom: 1px solid #e6ecf0;
  padding: 10px;
  display: flex;
  position: relative;
`;

const ProfileImageContainer = styled.div`
  width: 60px;
  height: 60px;
  margin-right: 10px;
  border-radius: 50px;
  overflow: hidden;
`;

const ProfileImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover; 
`;

const ContentInput = styled(TextArea)`
  width: 80%;
  border: none;
  font-size: 16px;
  min-height: 50px;
  max-height: 300px;
  margin-bottom: 50px;

  :disabled {
    background-color: white;
  }
`;

const IconContainer = styled.div`
  position: absolute;
  bottom: -3.5px;
  right: 107px;
  color: #1864ab;
  font-size: 38px;
  opacity: 0.5;
  cursor: pointer;
  &:hover {
    opacity: 1;
    transform: scale(0.98);
  }
`;

const SubmitButton = styled.button`
  all: unset;
  position: absolute;
  background-color: #1864ab;
  color: white;
  letter-spacing: 2px;
  font-weight: 600;
  font-size: 14px;
  width: 70px;
  height: 30px;
  border-radius: 6px;
  bottom: 10px;
  right: 30px;
  cursor: pointer;
  opacity: ${props => (props.isAvailablePosting ? 1 : 0.5)};
  transition: all 0.3s linear;
  display: flex;
  justify-content: center;
  align-items: center;
  outline: none;
  &:focus {
    outline: none;
  }
`;

const PostForm = () => {
  const { isLoggedIn } = useSelector((state) => state.user);

  const [content, setContent] = useState('');
  const onChangeContent = useCallback((e) => {
    const {target: {value}} = e;
    setContent(e.target.value);
    if (value.trim()) {
      setIsAvailablePosting(true);
    } else if (value.length === 0 || value.length > 500) {
      setIsAvailablePosting(false);
    };
  }, [content]);

  const imageInput = useRef();
  const onClickImageUpload = useCallback(() => {
    imageInput.current.click();
  }, [imageInput.current]);
  
  const onChangeImages = useCallback((e) => {
    const imageFormData = new FormData();
    [].forEach.call(e.target.files, f => {
      imageFormData.append("image", f);
    });
  });
  const [isAvailablePosting, setIsAvailablePosting] = useState(false);

  return (
    <Container>
      <FormContainer encType="multipart/form-data">
        <ProfileImageContainer>
          <ProfileImage src={require('../image/user.png')} />
        </ProfileImageContainer>
        <ContentInput 
          placeholder={`${
            isLoggedIn
              ? "오늘은 어떤 일이 있었나요?"
              : "로그인 후 사용해주세요."
          }`}
          disabled={isLoggedIn ? false : true}
          value={content}
          onChange={onChangeContent}
          style={{
            resize: "none",
            outline: "none",
            overflow: "hidden",
           }}
        />
        <input
          type="file"
          multiple
          hidden
          ref={imageInput}
          onChange={onChangeImages}
        ></input>
        <IconContainer onClick={onClickImageUpload}>
           <FaRegImage />
        </IconContainer>
        <SubmitButton type="submit" isAvailablePosting={isAvailablePosting}>
          게시
        </SubmitButton>
      </FormContainer>
    </Container>
  )
};

export default PostForm;
