import React, { useCallback, useState } from 'react';
import PropTypes from 'prop-types';
import { useDispatch } from 'react-redux';
import { REMOVE_IMAGE, UPDATE_POST_REQUEST } from '../reducers/post';
import TextArea from 'react-textarea-autosize';

import styled from 'styled-components';
import device from '../utils/device';

const Container = styled.div`
  display: flex;
  margin-top: 10px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  padding: 10px;
`;

const ContentInput = styled(TextArea)`
  width: 90%;
  border: none;
  font-size: 16px;
  min-height: 90px;
  max-height: 400px;
  margin-bottom: 10px;
  padding: 10px;
  border-radius: 10px;
  background-color: #f8f9fa;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  width: 93%;
`;

const ImageUploadContainer = styled.div`
  display: flex;
  width: 50%;
`;

const ImageContainer = styled.div`
  margin-right: 15px;
  position: relative;
`;

const Image = styled.img`
  width: 80px;
  height: 80px;
  margin-top: 5px;

  @media ${device.mobileL} {
    width: 40px;
    height: 40px;
  }
`;

const ImageDeleteContainer = styled.div`
  position: absolute;
  top: 2px;
  right: 1px;
  color: red;
  font-size: 20px;
  opacity: 0.7;
  cursor: pointer;
  &:hover {
    opacity: 1;
    transform: scale(0.98);
  }
`;

const SubmitButton = styled.button`
  all: unset;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 60px;
  height: 30px; 
  background-color: #1864ab;
  border-radius: 6px;
  color: white;
  cursor: pointer;
  outline: none;
  opacity: ${props => (props.isAvailablePosting ? 1 : 0.6)};
  transition: all 0.2s linear;
`;

const CancelButton = styled.button`
  all: unset;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 60px;
  height: 30px; 
  margin-left: 5px;
  background-color: #e8537c;
  border-radius: 6px;
  color: white;
  cursor: pointer;
  outline: none;
  opacity: 0.6;
  &:hover {
    opacity: 1;
  }
  transition: all 0.2s linear;
`;

const PostUpdateForm = ({ post, setEditMode }) => {
  const dispatch = useDispatch();

  const imagePaths = post.Images;
  const [contentInput, setContentInput] = useState(post.content);
  const [isAvailablePosting, setIsAvailablePosting] = useState(false);

  const onChangeContent = useCallback((e) => {
    const {target: {value}} = e;
    setContentInput(e.target.value);
    if (value.trim()) {
      setIsAvailablePosting(true);
    } else if (value.length === 0 || value.length > 500) {
      setIsAvailablePosting(false);
    };
  }, [contentInput]);

  const onCancelUpdate = useCallback(() => {
    setEditMode(false);
  }, []);

  const onRemoveImage = useCallback((index) => () => {
    dispatch({
      type: REMOVE_IMAGE,
      data: index,
    })
  });

  const onSubmit = useCallback(() => {
    if (!contentInput || !contentInput.trim() ) {
      return alert('글을 작성해주세요.')
    };
    
    if (contentInput.length > 500) {
      return alert('글자수가 너무 많습니다.');
    };

    dispatch({
      type: UPDATE_POST_REQUEST,
      data: {
        postId: post.id,
        content: contentInput,
      }
    });

    setEditMode(false);
  }, [contentInput]);

  return (
    <Container>
      <Form onSubmit={onSubmit}>
        <ContentInput
          value={contentInput}
          onChange={onChangeContent}
          style={{
            resize: "none",
            outline: "none",
            overflow: "hidden",
          }}
        />
        <ButtonContainer>
          <SubmitButton type="submit" isAvailablePosting={isAvailablePosting}>수정</SubmitButton>
          <CancelButton onClick={onCancelUpdate}>취소</CancelButton>
        </ButtonContainer>
        <ImageUploadContainer>
          {imagePaths && imagePaths.map((v, i) => (
            <ImageContainer key={v}>
              <Image 
                src={`http://localhost:3065/postImages/${v}`} 
                alt={v}
              />
              <ImageDeleteContainer onClick={onRemoveImage(i)}>
                <MdCancel />
              </ImageDeleteContainer>
            </ImageContainer>
          ))}
        </ImageUploadContainer>
      </Form>
    </Container>
  )
};

PostUpdateForm.propTypes = {
  post: PropTypes.object.isRequired,
  setEditMode: PropTypes.func.isRequired,
};

export default PostUpdateForm;
